(function(){BX.namespace("BX.Mail.UserSignature.List");BX.Mail.UserSignature.List={gridId:"mail-usersignature-grid"};BX.Mail.UserSignature.List.init=function(){BX.addCustomEvent("SidePanel.Slider:onMessage",function(e){if(e.getEventId()==="mail-add-signature"){var i=e.getData();var a=i.userSignatureId;if(a>0){var t=BX.Main.gridManager.getById(BX.Mail.UserSignature.List.gridId);if(t){t.instance.reloadTable("GET",{},function(){BX.Mail.UserSignature.List.highlightRow(a)})}}}})};BX.Mail.UserSignature.List.openUrl=function(e){if(BX.SidePanel){BX.SidePanel.Instance.open(e,{width:760,cacheable:false})}else{location.href=viewUrl}};BX.Mail.UserSignature.List.delete=function(e){if(confirm(BX.message("MAIL_SIGNATURE_DELETE_CONFIRM"))){BX.ajax.runAction("mail.api.usersignature.delete",{data:{userSignatureId:e}}).then(function(){BX.UI.Notification.Center.notify({content:BX.message("MAIL_SIGNATURE_DELETED_SUCCESS")});var e=BX.Main.gridManager.getById(BX.Mail.UserSignature.List.gridId);if(e){e.instance.reloadTable("GET")}},function(e){var i=new BX.UI.Alert({color:BX.UI.Alert.Color.DANGER,icon:BX.UI.Alert.Icon.DANGER,text:e.errors.join(", ")});BX.adjust(BX("signature-alert-container"),{html:""});BX.append(i.getContainer(),BX("signature-alert-container"))})}};BX.Mail.UserSignature.List.highlightRow=function(e){var i=BX.Main.gridManager.getById(BX.Mail.UserSignature.List.gridId);if(i){var a=i.instance.getRows().getById(e);if(a){a.select()}}}})();