BX.namespace("BX.Rest.Markeplace.CategoryRows");BX.Rest.Markeplace.CategoryRows={currentAppCode:null,init:function(){BX.addCustomEvent("Rest:AppLayout:ApplicationInstall",BX.proxy(function(e,t){this.changeButton()},this))},setCurrentApp:function(e){if(!e)return;this.currentAppCode=e},changeButton:function(){var e=document.querySelector("[data-id='btn-"+this.currentAppCode+"']");if(BX.type.isDomNode(e)){if(BX.hasClass(e,"ui-btn-success")){BX.addClass(e,"ui-btn-light-border");BX.removeClass(e,"ui-btn-success");e.innerHTML=BX.message("MARKETPLACE_CATEGORY_ROWS_DEINSTALL")}else{BX.addClass(e,"ui-btn-success");BX.removeClass(e,"ui-btn-light-border");e.innerHTML=BX.message("MARKETPLACE_CATEGORY_ROWS_INSTALL")}}}};